(980, 'calendar_addevent', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->calendar} - {$lang->add_event}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n	<div class=\"container-md\">\r\n{$event_errors}\r\n<form action=\"calendar.php\" method=\"post\" name=\"input\">\r\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n	<div class=\"card\">\r\n<div class=\"card-body\">\r\n\r\n<div class=\"pb-3 mb-2 border-bottom\">	\r\n<label for=\"name\">{$lang->event_name}</label>\r\n<input type=\"text\" id=\"name\" class=\"form-control border\" name=\"name\" maxlength=\"120\" value=\"{$name}\"/>\r\n</div>\r\n\r\n	\r\n<div class=\"pb-3 mb-2 border-bottom\">\r\n	<label>{$lang->event_date}</label>\r\n<div class=\"radio-toolbar\">\r\n<input type=\"radio\" name=\"type\" value=\"single\" {$type_single} id=\"type_single\" onclick=\"toggleType();\" />\r\n<label for=\"type_single\">{$lang->type_single}</label>	\r\n	\r\n	<script type=\"text/javascript\">\r\n				<!--\r\n					function toggleType()\r\n					{\r\n						if($(\"#type_single\").prop(\"checked\"))\r\n						{\r\n							$(\"#ranged_selects\").hide();\r\n							$(\"#single_selects\").show();\r\n						}\r\n						else\r\n						{\r\n							$(\"#single_selects\").hide();\r\n							$(\"#ranged_selects\").show();\r\n							toggleRepeats();\r\n						}\r\n					}\r\n\r\n					function toggleRepeats()\r\n					{\r\n						var repeats = $(\"#repeats\").get(0).selectedIndex;\r\n						$(\".repeats_type\").each(function()\r\n						{\r\n							$(this).hide();\r\n						});\r\n						if($(\"#repeats_type_\"+repeats).length > 0)\r\n						{\r\n							$(\"#repeats_type_\"+repeats).show();\r\n						}\r\n					}\r\n				// -->\r\n				</script>\r\n	\r\n	\r\n  <input type=\"radio\" name=\"type\" value=\"ranged\" {$type_ranged} id=\"type_ranged\" onclick=\"toggleType();\" />\r\n  <label for=\"type_ranged\"> {$lang->type_ranged}</label>\r\n	</div>\r\n	\r\n<div class=\"alert bg-nav mb-0 mt-2\" id=\"si