(1017, 'codebuttons', '<link rel=\"stylesheet\" href=\"{$BASEURL}/scripts/sceditor/default.min.css\" />\r\n<script src=\"{$BASEURL}/scripts/sceditor/sceditor.min.js\"></script>\r\n<script src=\"{$BASEURL}/scripts/sceditor/bbcode.js\"></script>\r\n<script src=\"{$BASEURL}/scripts/sceditor/monocons.js\"></script>\r\n<script src=\"{$BASEURL}/scripts/sceditor/undo.js\"></script>\r\n<script src=\"{$BASEURL}/scripts/sceditor/dragdrop.js\"></script>\r\n\r\n<script>\r\n\r\n\r\nfunction imgurUpload(file) {\r\n	var headers = new Headers({\r\n		\'authorization\': \'Client-ID cfa4cc7f6a54658\'\r\n	});\r\n\r\n	var form = new FormData();\r\n	form.append(\'image\', file);\r\n\r\n	return fetch(\'https://api.imgur.com/3/image\', {\r\n		method: \'post\',\r\n		headers: headers,\r\n		body: form\r\n	}).then(function (response) {\r\n		return response.json();\r\n	}).then(function (result) {\r\n		if (result.success) {\r\n			return result.data.link;\r\n		}\r\n\r\n		throw \'Upload error\';\r\n	});\r\n}\r\n\r\n\r\n\r\nvar dragdropOptions = {\r\n    // The allowed mime types that can be dropped on the editor\r\n    allowedTypes: [\'image/jpeg\', \'image/png\', \'image/jpg\', \'image/gif\'],\r\n    handleFile: function (file, createPlaceholder) {\r\n        var placeholder = createPlaceholder();\r\n\r\n        imgurUpload(file).then(function (url) {\r\n            // Replace the placeholder with the image HTML\r\n            placeholder.insert(\'<img src=\\\'\' + url + \'\\\' />\');\r\n        }).catch(function () {\r\n            // Error so remove the placeholder\r\n            placeholder.cancel();\r\n\r\n            alert(\'Problem uploading image to imgur.\');\r\n        });\r\n    }\r\n};\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n// Replace the textarea #example with SCEditor\r\nvar textarea = document.getElementById(\'message\');\r\nsceditor.create(textarea, {\r\n	plugins: \'undo, dragdrop\',\r\n	dragdrop: dragdropOptions,\r\n	format: \'bbcode\',\r\n	height: \'500\',\r\n	width: \'100%\',\r\n	\r\n	icons: \'monocons\',\r\n	style: \'https://localhost/scripts/sceditor/default.min3.css\',\r\n	\r\n	\r\n	\r\n\r\n	\r\n	\r\n	\r\n});\r\n</script>', 2, '1830', '', 1720609281),
