(1131, 'member_profile', '<!doctype html>\r\n<html lang=\"en\">\r\n<head>\r\n  <meta charset=\"utf-8\" />\r\n  <title>{$SITENAME} - {$lang->member[\'profile\']}</title>\r\n  <meta name=\"viewport\" content=\"width=device-width,initial-scale=1\" />\r\n  <script src=\"{$BASEURL}/scripts/report.js?ver=1820\"></script>\r\n\r\n  <style>\r\n    /* ---------- THEME ---------- */\r\n    :root{\r\n      --bg:#f6f8fb; --card:#ffffff; --muted:#6c7a87; --border:rgba(0,0,0,.06);\r\n      --ring:#dfe7ff; --accent:#3b82f6; --shadow:0 8px 20px rgba(16,24,40,.06), 0 2px 8px rgba(16,24,40,.04);\r\n      --radius:16px; --radius-sm:12px; --radius-lg:22px;\r\n      --title:#0f172a; --text:#1f2937;\r\n      --success:#12b76a; --warning:#f59e0b; --danger:#ef4444;\r\n    }\r\n    html{background:var(--bg); color:var(--text); font-family: system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, \"Helvetica Neue\", Arial, \"Noto Sans\", sans-serif;}\r\n    .container-md{max-width:1050px;}\r\n\r\n    /* ---------- UTILITIES ---------- */\r\n    .text-19{font-size:1.125rem}\r\n    .muted{color:var(--muted)}\r\n    .hov-soft{transition:transform .2s ease, box-shadow .2s ease}\r\n    .hov-soft:hover{transform:translateY(-2px); box-shadow:0 10px 22px rgba(16,24,40,.08)}\r\n    .shadow-soft{box-shadow:var(--shadow)}\r\n    .rounded-xl{border-radius:var(--radius)}\r\n    .rounded-2xl{border-radius:var(--radius-lg)}\r\n\r\n    /* ---------- CARDS ---------- */\r\n    .card-clean{border-radius:var(--radius); background:var(--card); border:1px solid var(--border); box-shadow:var(--shadow)}\r\n\r\n    /* ---------- HERO ---------- */\r\n    .profile-hero{ position:relative; border:1px solid var(--border); border-radius:var(--radius-lg); overflow:hidden; }\r\n    .profile-hero::before{ content:\"\"; position:absolute; inset:0; background:\r\n      radial-gradient(1200px 300px at -10% -40%, rgba(59,130,246,.08), transparent 60%),\r\n      radial-gradient(1000px 260px at 110% -30%, rgba(99,102,241,.08), transparent 60%),\r\n      linear-gradient(180deg, rgba(255,255,255,.86), rgba(255,255,255,.86));\r\n      pointer-events:none;\r\n    }\r\n    .profile-hero > .hero-inner{ position:relative; z-index:1 }\r\n\r\n    /* Keep Bootstrap blues out only inside HERO */\r\n    .profile-hero{ --bs-primary:#6c757d; --bs-primary-rgb:108,117,125; --bs-link-color:inherit; --bs-link-hover-color:inherit; --bs-focus-ring-color:rgba(108,117,125,.25) }\r\n    .profile-hero .text-primary, .profile-hero .link-primary{ color:inherit!important }\r\n    .profile-hero .bg-primary{ background-color:transparent!important }\r\n\r\n    /* ---------- AVATAR RING + STATUS DOT ---------- */\r\n    .avatar-ring{position:relative; display:inline-block; padding:6px; border-radius:50%; background:\r\n      conic-gradient(from 140deg,#fff 0 20%, var(--ring) 20% 70%, #fff 70% 100%)}\r\n    .avatar-ring>*{display:block; border-radius:50%}\r\n    .status-dot{ position:absolute; right:4px; bottom:4px; width:14px; height:14px; border-radius:50%; border:2px solid #fff; background:var(--success); box-shadow:0 0 0 2px #fff }\r\n    .status-away{ background:var(--warning) }\r\n    .status-off{ background:var(--muted) }\r\n\r\n    /* ---------- CHIPS / BADGES ---------- */\r\n    .chip{display:inline-flex; align-items:center; gap:.45rem; padding:.42rem .7rem; border-radius:999px;\r\n      font-weight:600; background:#f1f4f9; border:1px solid #e6ebf3; color:#445365; font-size:.8rem}\r\n    .badge-soft{display:inline-block; padding:.35rem .6rem; border-radius:.6rem; background:#f1f4f9; border:1px solid #e6ebf3; font-weight:600; font-size:.8rem; color:#5b6b7b}\r\n\r\n    /* ---------- METRICS ---------- */\r\n    .metric{border:1px solid var(--border); border-radius:12px; background:var(--card); padding:.85rem 1rem; box-shadow:var(--shadow)}\r\n    .metric .label{font-size:.8rem; color:var(--muted)}\r\n    .metric .value{font-weight:800; letter-spacing:.2px}\r\n    .ratio-ok{color:var(--success)} .ratio-warn{color:var(--warning)} .ratio-bad{color:var(--danger)}\r\n\r\n    /* ---------- LISTS ---------- */\r\n    .list-row{display:flex; justify-content:space-between; align-items:center; gap:1rem; padding:.72rem 0; border-bottom:1px dashed #e8ecf3}\r\n    .list-row:last-child{border-bottom:0}\r\n    .list-row:hover .muted{color:#2f3b46}\r\n\r\n    /* ---------- PROGRESS ---------- */\r\n    .progress{background:#eef2f7; border-radius:999px; overflow:hidden; height:6px}\r\n    .progress-bar{background:linear-gradient(90deg,#60a5fa,#3b82f6)}\r\n\r\n    /* ---------- TABS ---------- */\r\n    .nav-tabs{ gap:.35rem; border-bottom:1px solid var(--border) }\r\n    .nav-tabs .nav-link{border:none; background:transparent; color:#64748b; font-weight:700; border-radius:10px; padding:.55rem .8rem}\r\n    .nav-tabs .nav-link:hover{background:rgba(59,130,246,.08)}\r\n    .nav-tabs .nav-link.active{color:var(--title); background:#eef4ff}\r\n\r\n    /* ---------- STICKY SIDEBAR ---------- */\r\n    .sticky-col{ position:sticky; top:1rem }\r\n\r\n    /* ---------- DARK MODE ---------- */\r\n    @media (prefers-color-scheme: dark){\r\n      :root{--bg:#0b0f14; --card:#10161d; --muted:#99a6b4; --border:#1b2430; --ring:#253044; --accent:#6ea8fe; --shadow:none; --title:#e7ebf2; --text:#e6e8ea}\r\n      .chip{background:#16202c; border-color:#1f2a38; color:#a9c1ff}\r\n      .badge-soft{background:#1b2230; color:#70a1ff; border-color:#2a3442}\r\n      .list-row{border-bottom-color:#223042}\r\n      .progress{background:#17202a} .progress-bar{background:linear-gradient(90deg,#84b5ff,#6ea8fe)}\r\n      .nav-tabs .nav-link{color:#9aa3ac}\r\n      .nav-tabs .nav-link.active{color:#fff}\r\n    }\r\n	\r\n	\r\n	\r\n	\r\n\r\n/* Hover-подсказка */\r\n.avatar-overlay{\r\n  position:absolute; inset:0; display:flex; align-items:center; justify-content:center;\r\n  background:rgba(0,0,0,.35); color:#fff; font-weight:700; opacity:0; transition:.2s;\r\n}\r\n#avatar-container:hover .avatar-overlay{ opacity:1; }\r\n/* Прогресс */\r\n#avatar-progress{ position:absolute; left:50%; transform:translateX(-50%); bottom:-10px;\r\n  width:140px; height:6px; background:#eef2f7; border-radius:999px; overflow:hidden; display:none; }\r\n#avatar-progress-bar{ width:0%; height:100%; background:linear-gradient(90deg,#60a5fa,#3b82f6); }\r\n\r\n	\r\n	\r\n	\r\n#avatar-container[data-can-change=\"0\"] { cursor: default !important; }\r\n#avatar-container[data-can-change=\"0\"] .avatar-overlay { display: none !important; }\r\n#avatar-container[data-can-change=\"1\"] { cursor: pointer; }\r\n	\r\n\r\n\r\n	\r\n\r\n    /* ---------- ACCESSIBILITY ---------- */\r\n    @media (prefers-reduced-motion: reduce){ .hov-soft, .nav-tabs .nav-link{ transition:none } }\r\n  </style>\r\n</head>\r\n<body>\r\n<div class=\"container-md my-4\">\r\n  <!-- HERO -->\r\n  <div class=\"profile-hero p-3 p-md-4 mb-4 hov-soft\">\r\n    <div class=\"hero-inner\">\r\n      <div class=\"row g-3 g-lg-4 align-items-center\">\r\n        \r\n		\r\n	\r\n<div class=\"col-auto\">\r\n  <div class=\"avatar-ring position-relative hov-soft\"\r\n       id=\"avatar-container\"\r\n       data-uid=\"{$memprofile[\'id\']}\"\r\n       data-can-change=\"{$can_change_avatar}\"\r\n       title=\"Avatar\">\r\n    <div>\r\n      {$avatar}\r\n      {$status_dot_html}\r\n      <span class=\"avatar-overlay\">Change</span>\r\n    </div>\r\n\r\n    <div id=\"avatar-progress\"><div id=\"avatar-progress-bar\"></div></div>\r\n  </div>\r\n</div>\r\n\r\n<input type=\"file\" id=\"avatar-input\" accept=\"image/*\" style=\"display:none;\">\r\n		\r\n		\r\n\r\n\r\n        <div class=\"col\">\r\n          <div class=\"d-flex flex-wrap align-items-center gap-2\">\r\n            <h2 class=\"mb-0 me-1\">{$formattedname}</h2> {$usericons}\r\n          </div>\r\n          <div class=\"muted mt-1\">{$usertitle}</div>\r\n          <div class=\"mt-2\">{$groupimage}</div>\r\n          <div class=\"mt-2\">{$userstars}</div>\r\n\r\n          <div class=\"mt-3 d-flex flex-wrap gap-2\">\r\n            <span class=\"chip\"><i class=\"bi bi-upload me-1\"></i> Seeding: {$active_seeds}</span>\r\n            <span class=\"chip\"><i class=\"bi bi-download me-1\"></i> Leeching: {$active_leeches}</span>\r\n            <span class=\"chip\"><i class=\"bi bi-check2-circle me-1\"></i> Completed: {$times_completed_total}</span>\r\n          </div>\r\n        </div>\r\n\r\n        <div class=\"col-12 col-lg-3\">\r\n          <div class=\"d-grid gap-2\">\r\n            <a href=\"private.php?action=send&uid={$memprofile[\'id\']}\" class=\"btn btn-primary btn-sm\" aria-label=\"Send private message\">\r\n              <i class=\"bi bi-envelope me-1\"></i> Send PM\r\n            </a>\r\n            <a href=\"misc.php?action=buddy&add={$memprofile[\'id\']}\" class=\"btn btn-outline-secondary btn-sm\" aria-label=\"Add to buddy list\">\r\n              <i class=\"bi bi-person-plus me-1\"></i> Add to Buddy\r\n            </a>\r\n            {$report_options}\r\n          </div>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </div>\r\n\r\n  <!-- METRICS -->\r\n  <div class=\"row g-3 mb-4\">\r\n    <div class=\"col-12 col-md-3\">\r\n      <div class=\"metric text-center hov-soft\">\r\n        <div class=\"label\">Ratio</div>\r\n        <div class=\"value {$ratio_class}\" style=\"font-size:1.35rem;\">{$ratio}</div>\r\n      </div>\r\n    </div>\r\n    <div class=\"col-12 col-md-4\">\r\n      <div class=\"metric hov-soft\">\r\n        <div class=\"d-flex justify-content-between\">\r\n          <div class=\"label\"><i class=\"bi bi-arrow-up-right me-1\"></i>Uploaded</div>\r\n          <div class=\"value\">{$uploaded}</div>\r\n        </div>\r\n        <div class=\"progress mt-2\"><div class=\"progress-bar\" role=\"progressbar\" style=\"width: {$uploaded_percent}%\"></div></div>\r\n      </div>\r\n    </div>\r\n    <div class=\"col-12 col-md-4\">\r\n      <div class=\"metric hov-soft\">\r\n        <div class=\"d-flex justify-content-between\">\r\n          <div class=\"label\"><i class=\"bi bi-arrow-down-right me-1\"></i>Downloaded</div>\r\n          <div class=\"value\">{$downloaded}</div>\r\n        </div>\r\n        <div class=\"progress mt-2\"><div class=\"progress-bar\" role=\"progressbar\" style=\"width: {$downloaded_percent}%\"></div></div>\r\n      </div>\r\n    </div>\r\n  </div>\r\n\r\n  <div class=\"row g-4\">\r\n    <!-- LEFT -->\r\n    <div class=\"col-12 col-lg-8\">\r\n      <!-- Tabs -->\r\n      <ul class=\"nav nav-tabs mb-3\" role=\"tablist\">\r\n        <li class=\"nav-item\"><button class=\"nav-link active\" data-bs-toggle=\"tab\" data-bs-target=\"#tab-about\" type=\"button\">About</button></li>\r\n        <li class=\"nav-item\"><button class=\"nav-link\" data-bs-toggle=\"tab\" data-bs-target=\"#tab-forum\" type=\"button\">Forum</button></li>\r\n        <li class=\"nav-item\"><button class=\"nav-link\" data-bs-toggle=\"tab\" data-bs-target=\"#tab-torrents\" type=\"button\">Torrents</button></li>\r\n        <li class=\"nav-item\"><button class=\"nav-link\" data-bs-toggle=\"tab\" data-bs-target=\"#tab-contact\" type=\"button\">Contact</button></li>\r\n      </ul>\r\n\r\n      <div class=\"tab-content\">\r\n        <!-- ABOUT -->\r\n        <div class=\"tab-pane fade show active\" id=\"tab-about\">\r\n          {$awaybit}\r\n          <!-- красивый бан-блок: сформируй в PHP в переменную новым стилем -->\r\n          {$bannedbit}\r\n          {$signature}\r\n\r\n          \r\n\r\n          <div class=\"card-clean p-3 mt-3 hov-soft\">\r\n            <div class=\"row row-cols-1 row-cols-md-2 g-3\">\r\n              <div class=\"col\">\r\n                <div class=\"list-row\">\r\n                  <span class=\"muted\"><i class=\"bi bi-calendar-event me-1\"></i>{$lang->member[\'joined\']}</span>\r\n                  <span>{$memregdate}</span>\r\n                </div>\r\n                <div class=\"list-row\">\r\n                  <span class=\"muted\"><i class=\"bi bi-clock-history me-1\"></i>{$lang->member[\'lastvisit\']}</span>\r\n                  <span>{$memlastvisitdate}</span>\r\n                </div>\r\n                <div class=\"list-row\">\r\n                  <span class=\"muted\"><i class=\"bi bi-cake me-1\"></i>{$lang->member[\'date_of_birth\']}</span>\r\n                  <span>{$membday} {$membdayage}</span>\r\n                </div>\r\n                <div class=\"list-row\">\r\n                  <span class=\"muted\"><i class=\"bi bi-globe2 me-1\"></i>{$lang->member[\'local_time\']}</span>\r\n                  <span>{$localtime}</span>\r\n                </div>\r\n              </div>\r\n              <div class=\"col\">\r\n                {$invs}\r\n                <!-- соцсети / доп. факты сюда -->\r\n              </div>\r\n            </div>\r\n          </div>\r\n\r\n          \r\n		  \r\n		<!-- Status & time -->\r\n              <div class=\"card-clean p-0 mt-3 hov-soft\">\r\n                <div class=\"p-3 border-bottom d-flex align-items-center gap-2\">\r\n                  <i class=\"bi bi-activity\"></i>\r\n                  <strong>Status</strong>\r\n                </div>\r\n                <div class=\"p-3\">\r\n                  <div class=\"list-row\">\r\n                    <span class=\"muted d-flex align-items-center gap-2\">\r\n                      <i class=\"bi bi-circle-fill\" style=\"font-size:.6rem\"></i>\r\n                      {$lang->global[\'postbit_status\']}\r\n                    </span>\r\n                    <span>{$online_status}</span>\r\n                  </div>\r\n                  <div class=\"list-row\">\r\n                    <span class=\"muted d-flex align-items-center gap-2\">\r\n                      <i class=\"bi bi-clock-history\"></i>\r\n                      {$lang->member[\'timeonline\']}\r\n                    </span>\r\n                    <span>{$timeonline}</span>\r\n                  </div>\r\n                </div>\r\n              </div>\r\n		  \r\n		  \r\n		  \r\n		  \r\n        </div>\r\n\r\n        <!-- FORUM -->\r\n        <div class=\"tab-pane fade\" id=\"tab-forum\">\r\n          <div class=\"card-clean mb-3 hov-soft\">\r\n            <div class=\"p-3 border-bottom text-19 fw-bold d-flex align-items-center gap-2\">\r\n              <i class=\"bi bi-chat-dots-fill\"></i>\r\n              <span>{$users_forum_info}</span>\r\n            </div>\r\n\r\n            <div class=\"p-3\">\r\n              <!-- KPIs -->\r\n              <div class=\"row g-3\">\r\n                <!-- Total posts -->\r\n                <div class=\"col-12 col-md-6\">\r\n                  <div class=\"metric hov-soft d-flex align-items-center gap-3\">\r\n                    <div class=\"rounded-3 bg-light d-flex align-items-center justify-content-center\" style=\"width:42px;height:42px;\">\r\n                      <i class=\"bi bi-chat-left-text-fill\"></i>\r\n                    </div>\r\n                    <div class=\"flex-grow-1\">\r\n                      <div class=\"label d-flex justify-content-between align-items-center\">\r\n                        <span>{$lang->member[\'total_posts\']}</span>\r\n                        <span class=\"badge-soft\">{$ppd_percent_total}</span>\r\n                      </div>\r\n                      <div class=\"value mt-1\">{$memprofile[\'postnum\']}</div>\r\n                    </div>\r\n                    <div class=\"ms-2 d-none d-md-block\">\r\n                      {$findposts}\r\n                    </div>\r\n                  </div>\r\n                </div>\r\n\r\n                <!-- Total threads -->\r\n                <div class=\"col-12 col-md-6\">\r\n                  <div class=\"metric hov-soft d-flex align-items-center gap-3\">\r\n                    <div class=\"rounded-3 bg-light d-flex align-items-center justify-content-center\" style=\"width:42px;height:42px;\">\r\n                      <i class=\"bi bi-journal-text\"></i>\r\n                    </div>\r\n                    <div class=\"flex-grow-1\">\r\n                      <div class=\"label d-flex justify-content-between align-items-center\">\r\n                        <span>{$lang->member[\'total_threads\']}</span>\r\n                        <span class=\"badge-soft\">{$tpd_percent_total}</span>\r\n                      </div>\r\n                      <div class=\"value mt-1\">{$memprofile[\'threadnum\']}</div>\r\n                    </div>\r\n                    <div class=\"ms-2 d-none d-md-block\">\r\n                      {$findthreads}\r\n                    </div>\r\n                  </div>\r\n                </div>\r\n              </div>\r\n\r\n              <!-- Reputation & Referrals -->\r\n              <div class=\"row g-3 mt-1\">\r\n                <div class=\"col-12 col-lg-6\">\r\n                  <div class=\"card-clean p-0 hov-soft\">\r\n                    <div class=\"p-3 border-bottom d-flex align-items-center gap-2\">\r\n                      <i class=\"bi bi-heart-fill text-danger\"></i>\r\n                      <strong>Reputation</strong>\r\n                    </div>\r\n                    <div class=\"p-3\">{$reputation}</div>\r\n                  </div>\r\n                </div>\r\n\r\n                <div class=\"col-12 col-lg-6\">\r\n                  <div class=\"card-clean p-0 hov-soft\">\r\n                    <div class=\"p-3 border-bottom d-flex align-items-center gap-2\">\r\n                      <i class=\"bi bi-people-fill\"></i>\r\n                      <strong>Referrals</strong>\r\n                    </div>\r\n                    <div class=\"p-3\">{$referrals}</div>\r\n                  </div>\r\n                </div>\r\n              </div>\r\n\r\n              \r\n\r\n            </div>\r\n          </div>\r\n        </div>\r\n\r\n        <!-- TORRENTS -->\r\n        <div class=\"tab-pane fade\" id=\"tab-torrents\">\r\n          <div class=\"card-clean p-0 hov-soft mb-3\">\r\n            <div class=\"p-3 border-bottom text-19 fw-bold\">Latest torrents</div>\r\n        