(1147, 'member_profile_modoptions', '<!-- Moderator Options (compact) -->\r\n<div class=\"card-clean mb-4 hov-soft\">\r\n  <div class=\"p-3 border-bottom text-19 fw-bold d-flex align-items-center gap-2\">\r\n    <i class=\"bi bi-shield-check\"></i><span>Moderator Options</span>\r\n  </div>\r\n\r\n  <div class=\"p-3\">\r\n    <div class=\"list-row\">\r\n      <span class=\"muted d-flex align-items-center gap-2\">\r\n        <i class=\"bi bi-geo-alt-fill\"></i><span>IP Address</span>\r\n      </span>\r\n      <span>{$ipaddress}</span>\r\n    </div>\r\n\r\n    <div class=\"mt-3\">\r\n      <div class=\"muted mb-2 d-flex align-items-center gap-2\">\r\n        <i class=\"bi bi-tools\"></i><span>Quick actions</span>\r\n      </div>\r\n\r\n      \r\n      <ul id=\"mod-actions\" class=\"icon-grid m-0 list-unstyled\">\r\n        {$manageuser}\r\n      </ul>\r\n    </div>\r\n  </div>\r\n</div>\r\n\r\n<style>\r\n  .icon-grid{display:flex;flex-wrap:wrap;gap:.5rem}\r\n  .icon-grid a{\r\n    display:inline-flex;align-items:center;justify-content:center;\r\n    width:36px;height:36px;border-radius:10px;\r\n    border:1px solid #e5e7eb;background:#f9fafb;text-decoration:none;\r\n  }\r\n  .icon-grid a:hover{transform:translateY(-2px)}\r\n  @media (prefers-color-scheme: dark){\r\n    .icon-grid a{border-color:#1f2a38;background:#0f1720}\r\n  }\r\n</style>\r\n\r\n<script>\r\n(()=>{ // tiny mapper: подбираем иконку по тексту/URL\r\n  const map = [\r\n    [/ban/i,        \'bi-slash-circle\'],\r\n    [/unban/i,      \'bi-shield-check\'],\r\n    [/edit|profil/i,\'bi-pencil-square\'],\r\n    [/warn|предуп/i,\'bi-exclamation-triangle\'],\r\n    [/ip|whois/i,   \'bi-geo\'],\r\n    [/delete|удал/i,\'bi-trash3\'],\r\n    [/note|замет/i, \'bi-journal-text\'],\r\n    [/merge|объед/i,\'bi-diagram-3\'],\r\n    [/spam/i,       \'bi-bug\']\r\n  ];\r\n  document.querySelectorAll(\'#mod-actions a\').forEach(a=>{\r\n    const txt=(a.textContent||\'\').trim();\r\n    const href=a.getAttribute(\'href\')||\'\';\r\n    const rule = map.find(([re])=>re.test(txt)||re.test(href));\r\n    const icon = (rule && rule[1]) || \'bi-gear\';\r\n    a.innerHTML = \'<i class=\"bi \'+icon+\'\" aria-hidden=\"true\"></i><span class=\"visually-hidden\">\'+txt+\'</span>\';\r\n    a.title = txt;\r\n  });\r\n})();\r\n</script>\r\n', 2, '1837', '', 1720609281),
