(1226, 'modcp_editprofile', '<html>\r\n<head>\r\n<title>{$SITENAME} - {$lang->modcp[\'edit_profile\']}</title>\r\n{$headerinclude}\r\n<script type=\"text/javascript\">\r\n<!--\r\n	var mod_posts = \'{$modpost_check}\';\r\n	var susp_posts = \'{$suspost_check}\';\r\n// -->\r\n</script>\r\n</head>\r\n<body>\r\n	{$header}\r\n	<form action=\"modcp.php\" method=\"post\">\r\n		<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n			<div class=\"container-md\">\r\n		\r\n		<div class=\"row\">\r\n			\r\n			<div class=\"col-lg-3\">\r\n\r\n{$modcp_nav}\r\n				\r\n				</div>\r\n			<div class=\"col\">\r\n					{$errors}\r\n				\r\n				<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$edit_profile}\r\n	</div>\r\n	<div class=\"card-body pt-1\">	\r\n		\r\n		<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'profile_optional\']}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->modcp[\'profile_optional\']}\r\n</div>\r\n<div class=\"col\">\r\n{$requiredfields}\r\n\r\n\r\n		<input type=\"checkbox\" class=\"form-check-input\" name=\"remove_avatar\" value=\"1\" />\r\n  <label class=\"form-check-label\" for=\"remove_avatar\">\r\n	  {$lang->modcp[\'remove_avatar\']}</label>\r\n	\r\n	\r\n	\r\n</div>\r\n</div>\r\n	\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'birthday\']}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->modcp[\'birthday\']}\r\n</div>\r\n<div class=\"col\">\r\n<div class=\"alert bg-nav mb-0\">\r\n				<div class=\"row g-1\">\r\n					<div class=\"col-auto\">\r\n						<span class=\"small text-desc\">{$lang->modcp[\'day\']}</span>\r\n<select name=\"birthday_day\" class=\"form-select form-select-sm border pe-5\">\r\n<option value=\"\">&nbsp;</option>\r\n{$bdaydaysel}\r\n</select>\r\n					</div>\r\n					<div class=\"col-auto\">\r\n						<span class=\"small text-desc\">{$lang->month}</span>\r\n<select name=\"birthday_month\" class=\"form-select form-select-sm border pe-5\">\r\n<option value=\"\">&nbsp;</option>\r\n<option value=\"1\" {$bdaymonthsel[\'1\']}>{$lang->modcp[\'month_1\']}</option>\r\n<option value=\"2\" {$bdaymonthsel[\'2\']}>{$lang->modcp[\'month_2\']}</option>\r\n<option value=\"3\" {$bdaymonthsel[\'3\']}>{$lang->modcp[\'month_3\']}</option>\r\n<option value=\"4\" {$bdaymonthsel[\'4\']}>{$lang->modcp[\'month_4\']}</option>\r\n<option value=\"5\" {$bdaymonthsel[\'5\']}>{$lang->modcp[\'month_5\']}</option>\r\n<option value=\"6\" {$bdaymonthsel[\'6\']}>{$lang->modcp[\'month_6\']}</option>\r\n<option value=\"7\" {$bdaymonthsel[\'7\']}>{$lang->modcp[\'month_7\']}</option>\r\n<option value=\"8\" {$bdaymonthsel[\'8\']}>{$lang->modcp[\'month_8\']}</option>\r\n<option value=\"9\" {$bdaymonthsel[\'9\']}>{$lang->modcp[\'month_9\']}</option>\r\n<option value=\"10\" {$bdaymonthsel[\'10\']}>{$lang->modcp[\'month_10\']}</option>\r\n<option value=\"11\" {$bdaymonthsel[\'11\']}>{$lang->modcp[\'month_11\']}</option>\r\n<option value=\"12\" {$bdaymonthsel[\'12\']}>{$lang->modcp[\'month_12\']}</option>\r\n</select>\r\n					</div>\r\n	<div class=\"col-auto\">\r\n						<span class=\"small text-desc\">{$lang->year}</span>\r\n						<input type=\"text\" class=\"form-control form-control-sm border w-auto\" size=\"5\" maxlength=\"4\" name=\"birthday_year\" value=\"{$birthday_year}\" />\r\n					</div>\r\n				</div>\r\n	\r\n	</div>\r\n</div>\r\n</div>	\r\n\r\n	<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'website_url\']}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n			{$lang->modcp[\'website_url\']}\r\n			\r\n		</div>\r\n		<div class=\"col\">\r\n			\r\n<input type=\"text\" class=\"form-control form-control-sm border\" name=\"website\" maxlength=\"75\" value=\"{$user[\'website\']}\" />\r\n		</div>\r\n		\r\n		</div>	\r\n{$customfields}\r\n						\r\n		\r\n		<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'custom_usertitle\']}</div>\r\n	<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mb-2\">\r\n		<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n			{$lang->modcp[\'custom_usertitle\']}\r\n			\r\n		</div>\r\n		<div class=\"col\">\r\n			<span class=\"text-desc\">{$lang->modcp[\'custom_usertitle_note\']}</span>\r\n			\r\n			<div class=\"fw-bold fs-6 mb-3 mt-3 pb-3 border-bottom\">{$lang->modcp[\'default_usertitle\']} {$defaulttitle}</div>\r\n			\r\n			\r\n			\r\n			{$currentcustom}\r\n			\r\n			<label for=\"newtitle\">{$lang->modcp[\'new_custom_usertitle\']}</label>\r\n	<input type=\"text\" class=\"form-control form-control-sm border\" name=\"usertitle\" maxlength=\"{$customtitlemaxlength}\" value=\"{$newtitle}\" />\r\n			<input type=\"checkbox\" name=\"reverttitle\" id=\"reverttitle\" class=\"form-check-input\" /> {$lang->modcp[\'revert_usertitle\']}\r\n			\r\n		</div>\r\n		\r\n		</div>\r\n	\r\n\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'additional_contact_details\']}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->modcp[\'additional_contact_details\']}\r\n</div>\r\n<div class=\"col\">\r\n<div class=\"mb-3 pb-3 border-bottom\">\r\n		<label for=\"icq\">{$lang->modcp[\'icq_number\']}</label>\r\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"icq\" size=\"25\" value=\"{$user_icq}\" />\r\n	</div>\r\n	<div class=\"mb-3 pb-3 border-bottom\">\r\n		<label for=\"skype\">{$lang->modcp[\'skype_id\']}</label>\r\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"skype\" size=\"25\" value=\"{$user_skype}\" />\r\n	</div>\r\n	\r\n		<label for=\"google\">{$lang->modcp[\'google_id\']}</label>\r\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"google\" size=\"25\" value=\"{$user_google}\" />\r\n	\r\n</div>\r\n</div>\r\n		\r\n\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->modcp[\'moderation\']}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->modcp[\'moderation\']}\r\n</div>\r\n<div class=\"col\">\r\n<input type=\"checkbox\" name=\"moderateposting\" value=\"1\" {$modpost_checked} class=\"form-check-input\" onclick=\"toggleSuspend(\'modposts\');\" id=\"modpost\" /> {$lang->modcp[\'moderate_posts\']} <br />\r\n\r\n			<div id=\"modposts_action\" class=\"mt-2 mb-2\">\r\n{$moderateposts_info}\r\n			\r\n{$lang->modcp[\'modpost_length\']}\r\n				<div class=\"row\">\r\n					<div class=\"col align-self-center\">\r\n<input type=\"text\" name=\"modpost_time\" value=\"{$modpost_time}\" class=\"form-control border form-control-sm w-100\" />\r\n					</div>\r\n					<div class=\"col align-self-center\">\r\n						{$modpost_options}\r\n					</div>\r\n				</div>\r\n			</div>\r\n\r\n<input type=\"checkbox\" name=\"suspendposting\" value=\"1\" {$suspost_checked} class=\"form-check-input\" onclick=\"toggleSuspend(\'susposts\');\" id=\"suspost\" /> {$lang->modcp[\'suspend_posts\']}\r\n			\r\n			\r\n<div id=\"susposts_action\" class=\"mt-2\">\r\n\r\n{$suspendposting_info}\r\n\r\n{$lang->modcp[\'suspost_length\']}\r\n	<div class=\"row\">\r\n					<div class=\"col align-self-center\">\r\n<input type=\"text\" name=\"suspost_time\" value=\"{$suspost_time}\" class=\"form-control border form-control-sm w-100\" />\r\n					</div>\r\n					<div class=\"col align-self-center\">\r\n						{$suspost_options}\r\n					</div>\r\n				</div>\r\n			</div>\r\n</div>\r\n</div>\r\n{$awaysection}\r\n\r\n					</div>			\r\n<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$lang->modcp[\'signature\']}\r\n	</div>\r\n	<div class=\"card-body\">				\r\n	<textarea rows=\"15\" cols=\"70\" id=\"signature\" style=\"height: 250px\" class=\"form-control border form-control-sm\" name=\"signature\">{$user[\'signature\']}</textarea>\r\n{$codebuttons}\r\n\r\n{$suspend_signature}\r\n	</div>\r\n				</div>\r\n				\r\n<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$lang->modcp[\'mod_notes\']}\r\n	</div>\r\n	<div class=\"card-body\">				\r\n	<textarea name=\"usernotes\" id=\"usernotes\" rows=\"10\" cols=\"70\" class=\"form-control border form-control-sm\">{$user[\'usernotes\']}</textarea>\r\n	</div>\r\n	\r\n	<div class=\"card-footer text-center\">\r\n						<input type=\"hidden\" name=\"action\" value=\"do_editprofile\" />\r\n						<input type=\"hidden\" name=\"id\" value=\"{$user[\'id\']}\" />\r\n						<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->modcp[\'update_profile\']}\"><i class=\"fa-solid fa-sliders\"></i> &nbsp;{$lang->modcp[\'update_profile\']}</button>		\r\n	</div>\r\n				</div>				\r\n					\r\n\r\n					\r\n						\r\n	</form>\r\n	</div></div></div>\r\n\r\n	{$footer}\r\n<script type=\"text/javascript\">\r\n<!--\r\n	if(mod_posts == 0)\r\n	{\r\n		$(\"#modposts_action\").hide();\r\n	}\r\n\r\n	if(susp_posts == 0)\r\n	{\r\n		$(\"#susposts_action\").hide();\r\n	}\r\n\r\n	function toggleSuspend(action)\r\n	{\r\n		if(action == \"modposts\")\r\n		{\r\n			$(\"#susposts_action\").hide();\r\n			$(\"#suspost\").attr(\"checked\", false);\r\n\r\n			if($(\"#modpost\").prop(\"checked\") == false)\r\n			{\r\n				$(\"#modpost\").attr(\"checked\", false);\r\n				$(\"#modposts_action\").hide();\r\n			}\r\n			else\r\n			{\r\n				$(\"#modpost\").attr(\"checked\", true);\r\n				$(\"#modposts_action\").show();\r\n			}\r\n		}\r\n\r\n		if(action == \"susposts\")\r\n		{\r\n			$(\"#modposts_action\").hide();\r\n			$(\"#modpost\").attr(\"checked\", false);\r\n\r\n			if($(\"#suspost\").prop(\"checked\") == false)\r\n			{\r\n				$(\"#suspost\").attr(\"checked\", false);\r\n				$(\"#susposts_action\").hide();\r\n			}\r\n			else\r\n			{\r\n				$(\"#suspost\").attr(\"checked\", true);\r\n				$(\"#susposts_action\").show();\r\n			}\r\n		}\r\n	}\r\n// -->\r\n</script>\r\n</body>\r\n</html>', 2, '1837', '', 1720609281),
