(1228, 'modcp_editprofile_signature', '<script type=\"text/javascript\">\r\n<!--\r\n	var sig_checked = \"{$checked}\";\r\n\r\n	function toggleAction()\r\n	{\r\n		if($(\"#suspend_action\").is(\":visible\") == true)\r\n		{\r\n			$(\"#suspend_action\").hide();\r\n		}\r\n		else\r\n		{\r\n			$(\"#suspend_action\").show();\r\n		}\r\n	}\r\n// -->\r\n</script>\r\n<input type=\"checkbox\" name=\"suspendsignature\" value=\"1\" class=\"form-check-input\" {$checked_item} onclick=\"toggleAction();\" /> <span class=\"fw-normal\">{$lang->suspend_signature}</span>\r\n\r\n<div class=\"mt-2\" id=\"suspend_action\">\r\n{$suspendsignature_info}\r\n	\r\n{$lang->suspend_length}	\r\n<div class=\"row\">\r\n					<div class=\"col align-self-center\">\r\n<input type=\"text\" name=\"action_time\" value=\"{$action_time}\" class=\"form-control border form-control-sm w-100\" />\r\n					</div>\r\n					<div class=\"col align-self-center\">\r\n						{$action_options}\r\n					</div>\r\n				</div>\r\n</div>\r\n<script type=\"text/javascript\">\r\n<!--\r\n	if(sig_checked == 0)\r\n	{\r\n		$(\"#suspend_action\").hide();\r\n	}\r\n// -->\r\n</script>', 2, '1837', '', 1720609281),
