(1285, 'modcp_warninglogs', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->warning_logs}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n	{$header}\r\n	<div class=\"container-md\">\r\n		\r\n		<div class=\"row\">\r\n			\r\n			<div class=\"col-lg-3\">\r\n\r\n{$modcp_nav}\r\n			</div>\r\n			<div class=\"col\">\r\n\r\n	\r\n	<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$lang->warning_logs}\r\n	</div>\r\n	<div class=\"card-body pt-2 pb-0\">\r\n<div class=\"d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\r\n	<div class=\"row pb-2 fw-bold\">\r\n    <div class=\"col align-self-center\">{$lang->warned_user}</div>\r\n    <div class=\"col align-self-center\">{$lang->warning}</div>\r\n    <div class=\"col align-self-center\">{$lang->date_issued}</div>\r\n    <div class=\"col align-self-center\">{$lang->expires}</div>\r\n    <div class=\"col align-self-center\">{$lang->issued_by}</div>\r\n    <div class=\"col align-self-center\">{$lang->details}</div>\r\n</div>\r\n	</div>\r\n				\r\n					{$warning_list}\r\n				\r\n		</div>\r\n	</div>\r\n			\r\n				\r\n					\r\n					<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-bottom border-top-0 text-19 fw-bold\">\r\n		{$lang->filter_warning_logs}\r\n	</div>\r\n	<div class=\"card-body\">	\r\n		<form action=\"modcp.php\" method=\"get\">\r\n			<input type=\"hidden\" name=\"action\" value=\"warninglogs\" />\r\n\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->filter_warned_user}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->filter_warned_user}\r\n</div>\r\n<div class=\"col\">\r\n<input type=\"text\" name=\"filter[username]\" id=\"username\" value=\"{$filter_username}\" class=\"form-control border form-control-sm\" />\r\n</div>\r\n</div>\r\n		\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->filter_issued_by}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->filter_issued_by}\r\n</div>\r\n<div class=\"col\">\r\n<input type=\"text\" name=\"filter[mod_username]\" value=\"{$filter_modusername}\" class=\"form-control border form-control-sm\" />\r\n</div>\r\n</div>		\r\n		\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->filter_reason}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->filter_reason}\r\n</div>\r\n<div class=\"col\">\r\n<input type=\"text\" name=\"filter[reason]\" value=\"{$filter_reason}\" class=\"form-control border form-control-sm\" />\r\n</div>\r\n</div>	\r\n		\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->sort_by}</div>\r\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n{$lang->sort_by}\r\n</div>\r\n<div class=\"col\">\r\n<div class=\"pb-3 mb-3 border-bottom\">\r\n	<select name=\"filter[sortby]\" class=\"form-select border form-select-sm w-auto pe-5\">\r\n									<option value=\"username\"{$sortbysel[\'username\']}>{$lang->username}</option>\r\n									<option value=\"issuedby\"{$sortbysel[\'issuedby\']}>{$lang->issued_by}</option>\r\n									<option value=\"dateline\"{$sortbysel[\'dateline\']}>{$lang->issued_date}</option>\r\n									<o