(1291, 'moderation_delayedmoderation', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->delayed_moderation}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n	<div class=\"container-md\">\r\n		{$display_errors}\r\n\r\n	\r\n	<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$lang->delayed_mod_queue}\r\n	</div>\r\n	<div class=\"card-body pt-0 pb-0\">\r\n\r\n<div class=\"d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\r\n	<div class=\"row py-2 fw-bold\">\r\n    <div class=\"col align-self-center\">{$lang->mod_username}</div>\r\n    <div class=\"col align-self-center\">{$lang->time_to_perform_action}</div>\r\n    <div class=\"col align-self-center\">{$lang->mod_actions}</div>\r\n    <div class=\"col align-self-center\">{$lang->mod_information}</div>\r\n    <div class=\"col align-self-center\">{$lang->actions}</div>\r\n</div>\r\n	</div>	\r\n\r\n{$delayedmods}\r\n		\r\n\r\n		</div>\r\n		</div>\r\n<form action=\"moderation.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<input type=\"hidden\" name=\"action\" value=\"do_delayedmoderation\" />\r\n<input type=\"hidden\" name=\"url\" value=\"{$url}\" />\r\n<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\r\n<input type=\"hidden\" name=\"fid\" value=\"{$fid}\" />\r\n	\r\n	<div class=\"card mb-4\">\r\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\r\n		{$lang->delayed_moderation}\r\n	</div>\r\n	<div class=\"card-body\">\r\n\r\n	<div class=\"text-desc mb-3\">{$lang->delayed_moderation_desc}</div>\r\n\r\n	{$loginbox}\r\n		\r\n		\r\n\r\n		<strong>{$threads}</strong>\r\n\r\n		<div class=\"mt-3\">\r\n		\r\n		{$lang->run_moderation_time}\r\n<div class=\"row g-1\">\r\n	<div class=\"col-auto align-self-center\">\r\n		<select name=\"date_day\" class=\"form-select border form-select-sm pe-5 w-auto\">\r\n				{$dateday}\r\n		</select>\r\n	</div>\r\