(1343, 'newreply', '<html>\r\n<head>\r\n<title>{$post_reply_to}</title>\r\n{$headerinclude}\r\n{$post_javascript}\r\n		\r\n</head>\r\n<body>\r\n{$header}\r\n	\r\n\r\n	\r\n<div class=\"container-md\">\r\n{$forumrules}	\r\n{$preview}\r\n{$maximageserror}\r\n{$attacherror}\r\n{$reply_errors}\r\n{$moderation_notice}\r\n<form action=\"newreply.php?tid={$tid}&amp;processed=1\" method=\"post\" enctype=\"multipart/form-data\" name=\"input\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n\r\n<div id=\"fileIdsContainer\"></div>\r\n\r\n\r\n<div class=\"mb-3 p-0 pb-2 border-bottom\">				\r\n<input type=\"text\" class=\"form-control border mb-3\" name=\"subject\" maxlength=\"85\" value=\"{$subject}\" tabindex=\"1\" />\r\n</div>\r\n	\r\n{$codebuttons}\r\n\r\n<div class=\"row\">\r\n<div class=\"col\">\r\n<textarea id=\"message\" name=\"message\" class=\"form-control form-control-sm border\" style=\"height: 400px\" rows=\"20\" cols=\"70\" tabindex=\"2\" >{$message}</textarea>\r\n</div>\r\n{$smilieinserter}\r\n</div>	\r\n\r\n<div class=\"mt-2 mb-3\">\r\n{$postoptions}		\r\n{$modoptions}\r\n<a class=\"links\" data-bs-toggle=\"collapse\" aria-expanded=\"false\" aria-controls=\"collapse-1\" href=\"#collapse-attach\" role=\"button\"><i class=\"fa-solid fa-paperclip\"></i> &nbsp;{$lang->global[\'attachments\']}</a>&nbsp;		\r\n<button type=\"submit\" class=\"btn-thread\" name=\"previewpost\" value=\"{$lang->newreply[\'preview_post\']}\" tabindex=\"5\"><i class=\"fa-solid fa-pen\"></i> &nbsp;{$lang->newreply[\'preview_post\']}</button>\r\n{$savedraftbutton}		\r\n	</div>	\r\n	\r\n<!-- attach -->\r\n<div id=\"collapse-attach\" class=\"collapse mt-4\">\r\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">Attachments</div>\r\n<div class=\"row g-3 border-bottom m-auto pb-4 pt-0 mb-2\">\r\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-