(1512, 'showthread', '<html>\r\n<head>\r\n<title>{$thread[\'subject\']}</title>\r\n{$headerinclude}\r\n<script type=\"text/javascript\">\r\n<!--\r\n	var quickdelete_confirm = \"Are you sure you want to delete this post?\";\r\n	var quickrestore_confirm = \"Are you sure you want to restore this post?\";\r\n	var allowEditReason = \"1\";\r\n	lang.save_changes = \"Save Changes\";\r\n	lang.cancel_edit = \"Cancel Edit\";\r\n	lang.quick_edit_update_error = \"There was an error editing your reply:\";\r\n	lang.quick_reply_post_error = \"There was an error posting your reply:\";\r\n	lang.quick_delete_error = \"There was an error deleting your reply:\";\r\n	lang.quick_delete_success = \"The post was deleted successfully.\";\r\n	lang.quick_delete_thread_success = \"The thread was deleted successfully.\";\r\n	lang.quick_restore_error = \"There was an error restoring your reply:\";\r\n	lang.quick_restore_success = \"The post was restored successfully.\";\r\n	lang.editreason = \"Edit Reason\";\r\n	lang.post_deleted_error = \"You can not perform this action to a deleted post.\";\r\n	lang.softdelete_thread = \"Soft Delete Thread\";\r\n	lang.restore_thread = \"Restore Thread\";\r\n// -->\r\n</script>\r\n<!-- jeditable (jquery) -->\r\n<script src=\"{$BASEURL}/scripts/jeditable/jeditable-2.min.js\"></script>\r\n<script type=\"text/javascript\" src=\"{$BASEURL}/scripts/thread.js?ver=1827\"></script>\r\n\r\n\r\n</head>\r\n<body>\r\n{$header}\r\n\r\n	\r\n	\r\n<div class=\"container-md\">\r\n	\r\n\r\n	\r\n<div class=\"row mb-3 p-0 m-0\">\r\n	<div class=\"col-12 col-sm-12 col-md-12 col-lg col-xl col-xxl p-0 align-self-center text-center text-sm-center text-md-center text-lg-start text-xl-start text-xxl-start mt-3 mt-sm-3 mt-md-3 mt-lg-0 mt-xl-0 mt-xxl-0\">\r\n		{$multipage}\r\n	</div>\r\n	<div class=\"col-12 col-sm-12 col-md-12 col-lg-auto col-xl-auto col-xxl-auto m-0 p-0 text-center text-sm-center text-md-center text-lg-end text-xl-end text-xxl-end align-self-center order-first order-sm-fir