(1514, 'showthread_inlinemoderation', '<div class=\"col-lg\">\r\n<script type=\"text/javascript\" src=\"{$BASEURL}/scripts/inline_moderation.js?ver=1821\"></script>\r\n<form action=\"moderation.php\" method=\"post\" style=\"margin-top: 0; margin-bottom: 0;\" id=\"inlinemoderation_options\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\r\n<input type=\"hidden\" name=\"modtype\" value=\"inlinepost\" />\r\n	<div class=\"row g-1\">\r\n		<div class=\"col d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\r\n			&nbsp;\r\n		</div>\r\n		<div class=\"col-auto\">\r\n<select name=\"action\" id=\"inlinemoderation_options_selector\" class=\"form-select form-select-sm border w-auto pe-5\">\r\n{$standardposttools}\r\n{$customposttools}\r\n</select>\r\n		</div>\r\n		<div class=\"col-auto\">\r\n		\r\n<input type=\"submit\" class=\"btn btn-sm btn-primary\" name=\"go\" value=\"{$lang->showthread[\'go\']} ({$inlinecount})\" id=\"inline_go\" /> <input type=\"button\" class=\"btn btn-sm btn-primary\" onclick=\"inlineModeration.clearChecked();\" value=\"{$lang->showthread[\'clear\']}\" />\r\n</form>\r\n	</div>\r\n	</div>\r\n\r\n<script type=\"text/javascript\">\r\n<!--\r\n	var go_text = \"{$lang->showthread[\'inline_go\']}\";\r\n	var all_text = \"{$threadcount}\";\r\n	var inlineType = \"thread\";\r\n	var inlineId = {$tid};\r\n\r\n	$(\"#inlinemoderation_options_selector\").on(\'change\', function() {\r\n		$(\"#inlinemoderation_options\").trigger(\'submit\');\r\n	})\r\n// -->\r\n	</script>\r\n</div>', 2, '1837', '', 1720609281),
(1515, 'showthread_moderationoptions', '<div class=\"row mt-5 g-1 m-0 py-3 border-top border-bottom\">\r\n	<div class=\"col-lg-6\">\r\n\r\n<form action=\"moderation.php\" method=\"post\" style=\"margin-top: 0; margin-bottom: 0;\" id=\"moderator_options\">\r\n	<input type=\"hidden\" name=\"modtype\" value=\"thread\" />\r\n	<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\r\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<div class=\"row g-1 text-lg-start\">\r\n<div class=\"col-auto\">\r\n	<select name=\"action\" id=\"moderator_options_selector\" class=\"form-select form-select-sm w-auto pe-5\">\r\n		<option value=\"delayedmoderation\">{$lang->showthread[\'delayed_moderation\']}</option>\r\n		{$standardthreadtools}\r\n		{$customthreadtools}\r\n	</select>\r\n</div>\r\n<div class=\"col-auto\">\r\n{$gobutton}\r\n</div>\r\n</div>\r\n</form>\r\n<script type=\"text/javascript\">\r\n<!--\r\n	$(\"#moderator_options_selector\").on(\'change\', function() {\r\n		$(\"#moderator_options\").trigger(\'submit\');\r\n	})\r\n//-->\r\n</script>\r\n		\r\n	</div>\r\n{$inlinemod}\r\n	</div>', 2, '1837', '', 1720609281),
