(1518, 'showthread_poll', '<form action=\"polls.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<input type=\"hidden\" name=\"action\" value=\"vote\" />\r\n<input type=\"hidden\" name=\"pid\" value=\"{$poll[\'pid\']}\" />\r\n	\r\n				<div class=\"card border-0 mb-4\">\r\n	<div class=\"card-header rounded text-19 fw-bold\">\r\n		{$poll[\'question\']}<span class=\"badge bg-primary border border-white float-end text-uppercase small fw-normal\">{$lang->poll}</span>\r\n	</div>\r\n	<div class=\"card-body\">\r\n		<div class=\"row pb-3\">\r\n			<div class=\"col\">\r\n <span class=\"text-desc\">{$publicnote} {$closeon}</span>\r\n			</div>\r\n		</div>\r\n{$polloptions}\r\n		\r\n</div>\r\n					<div class=\"card-footer border-top-0 rounded\">\r\n						\r\n						<div class=\"row g-1\">\r\n							<div class=\"col-auto\">\r\n						 <button type=\"submit\" class=\"btn btn-primary btn-sm\" value=\"{$lang->vote}\">{$lang->vote}</button>\r\n							</div>\r\n							<div class=\"col text-end align-self-center\">\r\n								{$edit_poll} <a href=\"polls.php?action=showresults&amp;pid={$poll[\'pid\']}\"><i class=\"fa-solid fa-square-poll-horizontal\"></i> {$lang->show_results}</a>\r\n							</div>\r\n						</div>\r\n					\r\n				\r\n					\r\n				\r\n\r\n					</div></div>\r\n</form>', 2, '1837', '', 1720609281),
