(1523, 'showthread_poll_results', '<div class=\"card border-0 mb-4\">\r\n	<div class=\"card-header rounded text-19 fw-bold\">\r\n		{$poll[\'question\']}<span class=\"badge bg-primary border border-white float-end text-uppercase small fw-normal\">{$lang->poll}</span>\r\n	</div>\r\n	<div class=\"card-body\">\r\n		<div class=\"row pb-3\">\r\n			<div class=\"col\">\r\n <span class=\"text-desc\">{$pollstatus} {$undovote} {$publicnote} {$closeon}</span>\r\n			</div>\r\n		</div>\r\n{$polloptions}\r\n		<div class=\"text-end\"><span class=\"text-forum\">{$lang->total} {$lang->total_votes} {$totpercent}</span></div>\r\n</div>\r\n					<div class=\"card-footer border-top-0 rounded\">\r\n						\r\n						<div class=\"row g-1\">\r\n							<div class=\"col-auto\">\r\n						 <button type=\"submit\" class=\"btn btn-primary btn-sm\" value=\"{$lang->vote}\">{$lang->vote}</button>\r\n							</div>\r\n							<div class=\"col text-end align-self-center\">\r\n								{$edit_poll} &nbsp; <a href=\"polls.php?action=showresults&amp;pid={$poll[\'pid\']}\"><i class=\"fa-solid fa-square-poll-horizontal\"></i> {$lang->show_results}</a>\r\n							</div>\r\n						</div>\r\n				</div></div>', 2, '1837', '', 1720609281),
