(1525, 'showthread_quickreply', '<div id=\"quickreply_spinner\" class=\"showthread_spinner\" style=\"display: none\"><i class=\"fa-solid fa-circle-notch fa-spin\" style=\"color: #0b59e0;\"></i></div>\r\n\r\n{$moderation_notice}\r\n<form method=\"post\" action=\"newreply.php?tid={$tid}&amp;processed=1\" name=\"quick_reply_form\" id=\"quick_reply_form\">\r\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n	<input type=\"hidden\" name=\"subject\" value=\"RE: {$reply_subject}\" />\r\n	<input type=\"hidden\" name=\"action\" value=\"do_newreply\" />\r\n	<input type=\"hidden\" name=\"posthash\" value=\"{$posthash}\" id=\"posthash\" />\r\n	<input type=\"hidden\" name=\"quoted_ids\" value=\"\" id=\"quoted_ids\" />\r\n	<input type=\"hidden\" name=\"lastpid\" id=\"lastpid\" value=\"{$last_pid}\" />\r\n	<input type=\"hidden\" name=\"from_page\" value=\"{$page}\" />\r\n	<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\r\n	<input type=\"hidden\" name=\"method\" value=\"quickreply\" />\r\n	\r\n	\r\n	\r\n<div class=\"row d-flex g-2 mb-4 mt-5\">\r\n    <div class=\"col-auto d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\"><img src=\"{$CURUSER[\'avatar\']}\" class=\"rounded d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block img-fluid\" style=\"width: 100px;\"></div>\r\n    <div class=\"col\">\r\n		<div class=\"editor_control_bar mb-3 border border-top-5 p-2\" id=\"quickreply_multiquote\" style=\"display: none;\">\r\n					{$lang->showthread[\'quickreply_multiquote_selected\']} <a href=\"./newreply.php?tid={$tid}&amp;load_all_quotes=1\" onclick=\"return Thread.loadMultiQuoted();\">{$lang->showthread[\'quickreply_multiquote_now\']}</a> {$lang->showthread[\'or\']} <a href=\"javascript:void(0)\" onclick=\"Thread.clearMultiQuoted(); return false;\">{$lang->showthread[\'quickreply_multiquote_deselect\']}</a>.\r\n		</div>\r\n        <div class=\"card\">\r\n            <div class=\"card-body\">\r\n				<h6 class=\"mb-2\">{$CURUSER[\'username\']}</h6>\r\n		<textarea class=\"form-control form-control-sm border-0 p-0\" style=\"resize: none; height: 150px;\"  name=\"message\" id=\"message\" tabindex=\"1\" placeholder=\"Write a reply to this message...\"></textarea>\r\n			\r\n				\r\n	<div id=\"collapse-reply\" class=\"collapse bg-nav p-2\">\r\n{$closeoption}\r\n </div>\r\n				</div>\r\n			\r\n				<div class=\"card-footer border-top-0\">\r\n					<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->showthread[\'post_reply\']}\" tabindex=\"2\" accesskey=\"s\" id=\"quick_reply_submit\"><i class=\"fa-solid fa-comment\"></i> &nbsp;{$lang->showthread[\'post_reply\']}</button> <a class=\"btn btn-thread ms-3 me-3\" data-bs-toggle=\"collapse\" aria-expanded=\"false\" aria-controls=\"collapse-1\" href=\"#collapse-reply\" role=\"button\"><i class=\"fa-solid fa-gear\"></i></a> <button type=\"submit\" class=\"btn btn-thread\" name=\"previewpost\" value=\"{$lang->showthread[\'preview_post\']}\" tabindex=\"3\"><i class=\"fa-solid fa-pencil\"></i> &nbsp;{$lang->showthread[\'preview_post\']}</button>\r\n				</div>\r\n			</div>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	\r\n</form>', 2, '1837', '', 1720609281),
