(1550, 'usercp_avatar', '<html>\r\n<head>\r\n<title>{$SITENAME} - {$lang->usercp[\'change_avatar\']}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<form enctype=\"multipart/form-data\" action=\"usercp.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<div class=\"container-md\">\r\n<div class=\"row\">\r\n<div class=\"col-lg-3\">\r\n\r\n{$usercpnav}\r\n				\r\n</div>\r\n<div class=\"col\">\r\n{$avatar_error}	\r\n	\r\n	\r\n	\r\n	\r\n	<div class=\"card mb-4\">\r\n	<div class=\"card-body\">\r\n	\r\n	<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->usercp[\'change_avatar\']}</div>\r\n	<div class=\"row g-3 gx-5 mb-2\">\r\n		<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n			{$lang->usercp[\'change_avatar\']}\r\n			\r\n		</div>\r\n		<div class=\"col\">\r\n			\r\n			{$avatar_note}\r\n		</div>\r\n		<div class=\"col-auto text-center\">\r\n			{$currentavatar}\r\n		</div>\r\n		</div>\r\n		\r\n		<hr />\r\n		\r\n		{$avatarupload}\r\n		\r\n		\r\n			{$avatar_remote}\r\n\r\n	\r\n	</div>\r\n	<div class=\"card-footer text-center\">\r\n\r\n			<input type=\"hidden\" name=\"action\" value=\"do_avatar\" />\r\n			<button type=\"submit\" class=\"btn btn-primary\" name=\"submit\" value=\"{$lang->usercp[\'change_avatar\']}\"><i class=\"fa-solid fa-image\"></i> &nbsp;{$lang->usercp[\'change_avatar\']}</button>\r\n			{$removeavatar}\r\n			</div>\r\n	</div>\r\n			\r\n			\r\n			\r\n		</div>\r\n</form>\r\n	</div>\r\n	</div>\r\n</div>\r\n{$footer}\r\n</body>\r\n</html>', 2, '1837', '', 1720609281),
