(1557, 'usercp_changename', '<html>\r\n<head>\r\n<title>{$SITENAME} - {$lang->usercp[\'change_username\']}</title>\r\n\r\n</head>\r\n<body>\r\n\r\n<form action=\"usercp.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<div class=\"container-md\">\r\n<div class=\"row\">\r\n<div class=\"col-lg-3\">\r\n\r\n{$usercpnav}\r\n				\r\n</div>\r\n<div class=\"col\">\r\n				{$errors}\r\n	\r\n<div class=\"card\">\r\n<div class=\"card-body\">\r\n	\r\n	<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->usercp[\'change_username\']}</div>\r\n	<div class=\"row gx-5 g-3\">\r\n		<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\r\n			{$lang->usercp[\'change_username\']}\r\n			\r\n		</div>\r\n		<div class=\"col\">\r\n			\r\n<div class=\"pb-4 mb-3 border-bottom\">\r\n		<label>{$lang->usercp[\'password_confirmation\']}</label>\r\n		<input type=\"password\" class=\"form-control form-control-sm border\" name=\"password\" />\r\n			</div>\r\n			\r\n		\r\n	\r\n		<label>{$lang->usercp[\'new_username\']}</label>\r\n		<input type=\"text\" class=\"form-control form-control-sm border\" name=\"username\" maxlength=\"{$maxnamelength}\" value=\"{$username}\" />\r\n	\r\n		</div>\r\n		\r\n	</div>\r\n	</div>\r\n		<div class=\"card-footer text-center\">\r\n			<input type=\"hidden\" name=\"action\" value=\"do_changename\" />\r\n<button type=\"submit\" class=\"btn btn-primary\" name=\"submit\" value=\"{$lang->usercp[\'update_username\']}\"><i class=\"fa-solid fa-user-pen\"></i> &nbsp;{$lang->usercp[\'update_username\']}</button>\r\n		</div>\r\n	</div>\r\n	</form>\r\n	</div>\r\n	</div>\r\n	</div>\r\n		\r\n\r\n</body>\r\n</html>', 2, '1837', '', 1720609281),
