(1559, 'usercp_drafts', '<html>\r\n<head>\r\n<title>{$SITENAME} - {$lang->usercp[\'drafts\']}</title>\r\n\r\n</head>\r\n<body>\r\n{$header}\r\n<form action=\"usercp.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<div class=\"container-md\">\r\n<div class=\"row\">\r\n<div class=\"col-lg-3\">\r\n\r\n{$usercpnav}\r\n				\r\n</div>\r\n<div class=\"col\">\r\n	\r\n\r\n			\r\n<div class=\"card mb-4\">\r\n	<div class=\"card-header bg-white text-dark border-bottom-0 text-19 fw-bold mt-2 pb-0\">\r\n		{$drafts_count} <span class=\"float-end\"><input type=\"checkbox\" name=\"allbox\" class=\"form-check-input checkall\" /></span>\r\n	</div>\r\n	<div class=\"card-body\">\r\n\r\n	{$drafts}\r\n		\r\n	</div>\r\n	<div class=\"card-footer text-center\">\r\n		\r\n		<input type=\"hidden\" name=\"action\" value=\"do_drafts\" />\r\n<button type=\"submit\" class=\"btn btn-primary\" name=\"draftman\" value=\"{$lang->usercp[\'delete_drafts\']}\" {$disable_delete_drafts}><i class=\"fa-solid fa-xmark\"></i> &nbsp;{$lang->usercp[\'delete_drafts\']}</button>\r\n		\r\n	</div>\r\n	</div>\r\n	</form>\r\n	</div>\r\n	</div>\r\n</div>\r\n\r\n{$footer}\r\n</body>\r\n</html>', 2, '1837', '', 1720609281),
