(1562, 'usercp_editlists', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <title>{$SITENAME} - {$lang->usercp[\'edit_lists\']}</title>\r\n    \r\n    <script type=\"text/javascript\" src=\"{$BASEURL}/scripts/usercp.js?ver=1827\"></script>\r\n    <script type=\"text/javascript\">\r\n        lang.remove_buddy = \'{$lang->usercp[\'confirm_remove_buddy\']}\';\r\n        lang.remove_ignored = \'{$lang->usercp[\'confirm_remove_ignored\']}\';\r\n        lang.adding_buddy = \'{$lang->usercp[\'adding_buddy\']}\';\r\n        lang.adding_ignored = \'{$lang->usercp[\'adding_ignored\']}\';\r\n        lang.buddylist_error = \'{$lang->usercp[\'buddylist_error\']}\';\r\n    </script>\r\n\r\n    <!-- Обновленные Select2 файлы через CDN -->\r\n   \r\n   <script src=\"{$BASEURL}/scripts/select2/select2.min.js\"></script>\r\n   <link rel=\"stylesheet\" href=\"{$BASEURL}/scripts/select2/select2.min.css\">\r\n    \r\n    <style>\r\n        :root {\r\n            --primary-color: #3498db;\r\n            --secondary-color: #2c3e50;\r\n            --success-color: #2ecc71;\r\n            --danger-color: #e74c3c;\r\n            --light-bg: #f8f9fa;\r\n            --border-color: #dee2e6;\r\n        }\r\n        \r\n        \r\n        \r\n        \r\n        \r\n        .section-title {\r\n            font-size: 1.1rem;\r\n            font-weight: 600;\r\n            color: var(--secondary-color);\r\n            margin-bottom: 15px;\r\n            padding-bottom: 10px;\r\n            border-bottom: 1px solid var(--border-color);\r\n        }\r\n        \r\n        .form-label {\r\n            font-weight: 500;\r\n            margin-bottom: 8px;\r\n            color: #444;\r\n        }\r\n        \r\n        .help-text {\r\n            font-size: 0.85rem;\r\n            color: #6c757d;\r\n            margin-top: 5px;\r\n        }\r\n        \r\n        .btn-primary {\r\n            background-color: var(--primary-color);\r\n            border: none;\r\n            padding: 8px 18px;\r\n            font-weight: 500;\r\n        }\r\n        \r\n        .btn-primary:hover {\r\n            background-color: #2980b9;\r\n        }\r\n        \r\n        .btn-success {\r\n            background-color: var(--success-color);\r\n            border: none;\r\n            padding: 8px 18px;\r\n            font-weight: 500;\r\n        }\r\n        \r\n        .btn-danger {\r\n            background-color: var(--danger-color);\r\n            border: none;\r\n            padding: 8px 18px;\r\n            font-weight: 500;\r\n        }\r\n        \r\n        .empty-state {\r\n            text-align: center;\r\n            padding: 30px;\r\n            color: #6c757d;\r\n        }\r\n        \r\n        .empty-state i {\r\n            font-size: 3rem;\r\n            margin-bottom: 15px;\r\n            color: #ced4da;\r\n        }\r\n        \r\n        .select2-user-result {\r\n            display: flex;\r\n            align-items: center;\r\n            gap: 8px;\r\n            padding: 6px 12px;\r\n        }\r\n        \r\n        .select2-user-selection {\r\n            display: flex;\r\n            align-items: center;\r\n            gap: 6px;\r\n        }\r\n        \r\n        .select2-user-avatar {\r\n            width: 24px;\r\n            height: 24px;\r\n            border-radius: 50%;\r\n            object-fit: cover;\r\n        }\r\n        \r\n        .select2-user-avatar-sm {\r\n            width: 18px;\r\n            height: 18px;\r\n            border-radius: 50%;\r\n            object-fit: cover;\r\n        }\r\n        \r\n        .select2-user-name {\r\n            font-weight: 500;\r\n        }\r\n        \r\n        .select2-container--default .select2-selection--multiple {\r\n            min-height: 42px;\r\n            border: 1px solid #dcdfe6;\r\n            border-radius: 6px;\r\n            transition: all 0.2s ease;\r\n        }\r\n        \r\n        .select2-container--default.select2-container--focus .select2-selection--multiple {\r\n            border-color: var(--primary-color);\r\n            box-shadow: 0 0 0 2px rgba(52, 152, 219, 0.2);\r\n        }\r\n        \r\n        .select2-container--default .select2-selection--multiple .select2-selection__choice {\r\n            background-color: #409eff;\r\n            border-color: #409eff;\r\n            color: white;\r\n            border-radius: 4px;\r\n            padding: 2px 8px;\r\n            font-size: 13px;\r\n        }\r\n        \r\n        .select2-container--default .select2-selection--multiple .select2-selection__choice__remove {\r\n            color: white;\r\n            margin-right: 4px;\r\n        }\r\n        \r\n        .user-list-container {\r\n            margin-top: 20px;\r\n        }\r\n        \r\n        .user-item {\r\n            display: flex;\r\n            justify-content: space-between;\r\n            align-items: center;\r\n            padding: 12px 15px;\r\n            border-bottom: 1px solid var(--border-color);\r\n            transition: background-color 0.2s;\r\n        }\r\n        \r\n        .user-item:hover {\r\n            background-color: #f8f9fa;\r\n        }\r\n        \r\n        .user-item:last-child {\r\n            border-bottom: none;\r\n        }\r\n        \r\n     \r\n        \r\n        /* Улучшения для мобильной версии */\r\n        .bg-nav {\r\n            background-color: var(--secondary-color);\r\n            color: white;\r\n        }\r\n        \r\n        .input-group {\r\n            display: flex;\r\n            flex-direction: column;\r\n            gap: 10px;\r\n        }\r\n        \r\n        @media (min-width: 992px) {\r\n            .input-group {\r\n                flex-direction: row;\r\n                align-items: end;\r\n            }\r\n            \r\n            .input-group .form-control {\r\n                flex: 1;\r\n            }\r\n        }\r\n        \r\n        /* Стили для ограниченного placeholder */\r\n        .select2-selection__placeholder {\r\n            max-width: 180px;\r\n            overflow: hidden;\r\n            text-overflow: ellipsis;\r\n            white-space: nowrap;\r\n            display: block;\r\n            color: #6c757d;\r\n            font-size: 0.875rem;\r\n        }\r\n\r\n        .select2-container--default .select2-selection--multiple .select2-selection__placeholder {\r\n            margin-left: 8px;\r\n            margin-top: 5px;\r\n        }\r\n\r\n        /* Для поля поиска внутри select2 */\r\n        .select2-search__field::placeholder {\r\n            font-size: 0.875rem;\r\n            color: #6c757d;\r\n            max-width: 120px;\r\n            overflow: hidden;\r\n            text-overflow: ellipsis;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n    \r\n    <form action=\"usercp.php\" method=\"post\" id=\"buddy\" onsubmit=\"return UserCP.addBuddy(\'buddy\');\">\r\n        <input type=\"hidden\" name=\"action\" value=\"do_editlists\" />\r\n        <input type=\"hidden\" name=\"manage\" value=\"buddy\" />\r\n        <input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n        \r\n        <div class=\"container-md\">\r\n            <div class=\"row\">\r\n                <div class=\"col-lg-3\">\r\n                    {$usercpnav}\r\n                </div>\r\n                <div class=\"col\">    \r\n                    \r\n                    <div class=\"card mb-4\">\r\n                        <div class=\"card-header\">\r\n                            <i class=\"fas fa-user-friends me-2\"></i> {$lang->usercp[\'edit_buddy_list\']}\r\n                        </div>\r\n                        <div class=\"card-body\">\r\n                            \r\n                            <div class=\"mb-4\">\r\n                                <div class=\"section-title\">{$lang->usercp[\'add_buddies\']}</div>\r\n                                <div class=\"mb-3\">\r\n                                    <label class=\"form-label\">{$lang->usercp[\'username_or_usernames\']}</label>\r\n                                    <div class=\"help-text\">{$lang->usercp[\'add_buddies_desc\']}</div>\r\n                                    <select name=\"add_username\" id=\"buddy_add_username\" class=\"form-control\" multiple=\"multiple\"></select>\r\n                                </div>\r\n                                <div class=\"d-flex justify-content-between align-items-center\">\r\n                                    <button type=\"button\" class=\"btn btn-primary\" id=\"buddy_search_btn\">\r\n                                        <i class=\"fas fa-search me-1\"></i> {$lang->usercp[\'search_user\']}\r\n                                    </button>\r\n                                    <button type=\"submit\" id=\"buddy_submit\" class=\"btn btn-success\">\r\n                                        <i class=\"fas fa-user-plus me-1\"></i> {$lang->usercp[\'add_to_buddies\']}\r\n                                    </button>\r\n                                </div>\r\n                            </div>\r\n                            \r\n                            <div class=\"user-list-container\">\r\n                                <div class=\"section-title\">{$current_buddies}</div>\r\n                                {$buddy_list}\r\n                            </div>\r\n                            \r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </form>\r\n    \r\n    <form action=\"usercp.php\" method=\"post\" id=\"ignored\" onsubmit=\"return UserCP.addBuddy(\'ignored\');\">\r\n        <input type=\"hidden\" name=\"action\" value=\"do_editlists\" />\r\n        <input type=\"hidden\" name=\"manage\" value=\"ignored\" />\r\n        <input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n        \r\n        \r\n		<div class=\"container mt-3\">\r\n		<div class=\"card mb-4\">\r\n            <div class=\"card-header\">\r\n                <i class=\"fas fa-user-slash me-2\"></i> {$lang->usercp[\'edit_ignore_list\']}\r\n            </div>\r\n            <div class=\"card-body\">\r\n                \r\n                <div class=\"mb-4\">\r\n                    <div class=\"section-title\">{$lang->usercp[\'add_ignored_users\']}</div>\r\n                    <div class=\"mb-3\">\r\n                        <label class=\"form-label\">{$lang->usercp[\'username_or_usernames\']}</label>\r\n                        <div class=\"help-text\">{$lang->usercp[\'add_ignored_users_desc\']}</div>\r\n                        <select name=\"add_username\" id=\"ignored_add_username\" class=\"form-control\" multiple=\"multiple\"></select>\r\n                    </div>\r\n                    <div class=\"d-flex justify-content-between align-items-center\">\r\n                        <button type=\"button\" class=\"btn btn-primary\" id=\"ignored_search_btn\">\r\n                            <i class=\"fas fa-search me-1\"></i> {$lang->usercp[\'search_user\']}\r\n                        </button>\r\n                        <button type=\"submit\" id=\"ignored_submit\" class=\"btn btn-danger\">\r\n                            <i class=\"fas fa-ban me-1\"></i> {$lang->usercp[\'ignore_users\']}\r\n                        </button>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class=\"user-list-container\">\r\n                    <div class=\"section-title\">{$current_ignored_users}</div>\r\n                    {$ignore_list}\r\n                </div>\r\n                \r\n            </div>\r\n        </div>\r\n		 </div>\r\n    </form>\r\n    \r\n    <div class=\"container mt-3\">\r\n	{$received_requests}\r\n    {$sent_requests}\r\n	</div>\r\n    \r\n    <script>\r\n    // Современная инициализация Select2 с исправлением ошибки args\r\n    document.addEventListener(\'DOMContentLoaded\', function() {\r\n        function initUserSelect(selector, placeholder) {\r\n            if ($(selector).length && typeof $.fn.select2 !== \'undefined\') {\r\n                // Ограничиваем длину placeholder\r\n                const maxPlaceholderLength = 25;\r\n                const safePlaceholder = placeholder && placeholder.length > maxPlaceholderLength \r\n                    ? placeholder.substring(0, maxPlaceholderLength) + \'...\' \r\n                    : placeholder;\r\n\r\n                $(selector).select2({\r\n                    placeholder: safePlaceholder || \"Поиск...\",\r\n                    minimumInputLength: 2,\r\n                    maximumSelectionLength: 5,\r\n                    multiple: true,\r\n                    width: \'100%\',\r\n                    allowClear: true,\r\n                    closeOnSelect: false,\r\n                    language: {\r\n                        noResults: function() {\r\n                            return \"Ничего не найдено\";\r\n                        },\r\n                        searching: function() {\r\n                            return \"Поиск...\";\r\n                        },\r\n                        // ИСПРАВЛЕНИЕ: Правильное использование параметров\r\n                        inputTooShort: function(params) {\r\n                            const remaining = params.minimum - params.input.length;\r\n                            if (remaining === 1) {\r\n                                return \"Введите еще 1 символ\";\r\n                            } else if (remaining > 1 && remaining <= 4) {\r\n                                return \"Введите еще \" + remaining + \" символа\";\r\n                            } else {\r\n                                return \"Введите еще \" + remaining + \" символов\";\r\n                            }\r\n                        },\r\n                        maximumSelected: function(params) {\r\n                            return \"Максимум можно выбрать \" + params.maximum + \" пользователей\";\r\n                        }\r\n                    },\r\n                    ajax: {\r\n                        url: \"xmlhttp.php?action=get_users\",\r\n                        dataType: \'json\',\r\n                        delay: 300,\r\n                        cache: true,\r\n                        data: function(params) {\r\n                            return {\r\n                                query: params.term,\r\n                                page: params.page || 1\r\n                            };\r\n                        },\r\n                        processResults: function(data) {\r\n                            return {\r\n                                results: data.results || data,\r\n                                pagination: {\r\n                                    more: data.more || false\r\n                                }\r\n                            };\r\n                        }\r\n                    },\r\n                    templateResult: function(user) {\r\n                        if (user.loading) return user.text;\r\n                        \r\n                        // Ограничиваем текст в результатах поиска\r\n                        const maxResultLength = 35;\r\n                        const text = user.text && user.text.length > maxResultLength \r\n                            ? user.text.substring(0, maxResultLength) + \'...\' \r\n                            : user.text;\r\n\r\n                        if (user.avatar) {\r\n                            return $(\r\n                                \'<div class=\"select2-user-result\">\' +\r\n                                    \'<img src=\"\' + user.avatar + \'\" class=\"select2-user-avatar\" />\' +\r\n                                    \'<span class=\"select2-user-name\">\' + text + \'</span>\' +\r\n                                \'</div>\'\r\n                            );\r\n                        }\r\n                        \r\n                        return text || user.id;\r\n                    },\r\n                    templateSelection: function(user) {\r\n                        // Ограничиваем текст в выбранных элементах\r\n                        const maxSelectionLength = 20;\r\n                        const text = user.text && user.text.length > maxSelectionLength \r\n                            ? user.text.substring(0, maxSelectionLength) + \'...\' \r\n                            : user.text;\r\n\r\n                        if (user.avatar) {\r\n                            return $(\r\n                                \'<div class=\"select2-user-selection\">\' +\r\n                                    \'<img src=\"\' + user.avatar + \'\" class=\"select2-user-avatar-sm\" />\' +\r\n                                    \'<span class=\"select2-user-name\">\' + text + \'</span>\' +\r\n                                \'</div>\'\r\n                            );\r\n                        }\r\n                        return text || user.id;\r\n                    }\r\n                }).on(\'select2:select\', function(e) {\r\n                    // Сохраняем выбранные значения в localStorage\r\n                    const selectedData = $(this).select2(\'data\');\r\n                    localStorage.setItem($(this).attr(\'id\') + \'_selected\', JSON.stringify(selectedData));\r\n                }).on(\'select2:unselect\', function(e) {\r\n                    // Обновляем localStorage при удалении выбора\r\n                    const selectedData = $(this).select2(\'data\');\r\n                    localStorage.setItem($(this).attr(\'id\') + \'_selected\', JSON.stringify(selectedData));\r\n                });\r\n            }\r\n        }\r\n\r\n        // Восстановление выбранных значений из localStorage\r\n        function restoreSelections() {\r\n            [\'buddy_add_username\', \'ignored_add_username\'].forEach(function(id) {\r\n                const saved = localStorage.getItem(id + \'_selected\');\r\n                if (saved) {\r\n                    try {\r\n                        const data = JSON.parse(saved);\r\n                        if (data && data.length > 0) {\r\n                            const values = data.map(item => item.id);\r\n                            $(\'#\' + id).val(values).trigger(\'change\');\r\n                            \r\n                            // Вручную добавляем данные в select2 для отображения\r\n                            const select2 = $(\'#\' + id).data(\'select2\');\r\n                            if (select2) {\r\n                                select2.dataAdapter.current(function(data) {\r\n                                    data.push.apply(data, values);\r\n                                });\r\n                            }\r\n                        }\r\n                    } catch (e) {\r\n                        console.error(\'Error restoring selection for \' + id, e);\r\n                    }\r\n                }\r\n            });\r\n        }\r\n\r\n        // Инициализация с безопасным placeholder\r\n        const searchText = \"{$lang->usercp[\'search_user\']}\" || \"Поиск пользователя\";\r\n        initUserSelect(\"#buddy_add_username\", searchText);\r\n        initUserSelect(\"#ignored_add_username\", searchText);\r\n        \r\n        // Восстанавливаем выбранные значения после инициализации\r\n        setTimeout(restoreSelections, 100);\r\n        \r\n        // Обработчики для кнопок поиска\r\n        $(\'#buddy_search_btn\').on(\'click\', function(e) {\r\n            e.preventDefault();\r\n            $(\'#buddy_add_username\').select2(\'open\');\r\n        });\r\n        \r\n        $(\'#ignored_search_btn\').on(\'click\', function(e) {\r\n            e.preventDefault();\r\n            $(\'#ignored_add_username\').select2(\'open\');\r\n        });\r\n        \r\n        // Очистка localStorage при отправке формы\r\n        $(\'form\').on(\'submit\', function() {\r\n            localStorage.removeItem(\'buddy_add_username_selected\');\r\n            localStorage.removeItem(\'ignored_add_username_selected\');\r\n        });\r\n    });\r\n    </script>\r\n</body>\r\n</html>', 2, '1837', '', 1720609281),
